<script>
export default {
  components: {
    sideNav: () => import('~/components/sideNavContact')
  },
  props: ['blok'],
  data () {
    return {
      clicked: false,
      loaded: false
    }
  },
  mounted () {
    document.addEventListener('click', (e) => {
      const target = e.target.getAttribute('id')
      const targetClass = e.target.getAttribute('class')
      console.log(target)
      console.log(targetClass)
      if (targetClass === 'side-nav-item blackText') {
        setTimeout(() => { this.clicked = false }, 1000)
      }
      if (target === 'side-nav-contact-wrap') {
        this.clicked = false
      }
      if (target === 'film2') {
        setTimeout(() => {
          this.clicked = false
          this.scrollToVideos()
        }, 1000)
      }
    })
    // --- Mobile support --- //
    document.addEventListener('touchstart', (e) => {
      const target = e.target.getAttribute('id')
      const targetClass = e.target.getAttribute('class')
      if (targetClass === 'side-nav-item whiteText') {
        setTimeout(() => { this.clicked = false }, 1000)
      }
      if (target === 'side-nav-contact-wrap') {
        this.clicked = false
      }
      if (target === 'film2') {
        setTimeout(() => {
          this.scrollToVideos()
          this.clicked = false
        }, 1000)
      }
    })
    this.loaded = true
    /* --- does not work - when any touch move is made, everything lights up black
    document.addEventListener('touchstart', (event) => {
      const paths = event.target.querySelectorAll('path')
      paths.forEach((path) => {
        path.classList.add('blackText')
      })
    })
    */
  },
  methods: {
    scrollToVideos () {
      const elmntToView = document.getElementById('video-wrap')
      elmntToView.scrollIntoView({ block: 'start', behavior: 'smooth' })
    },
    black (event) {
      if (event.target.id === 'hello-wonder') {
        const helloWonder = document.getElementById('hello-wonder')
        const paths = helloWonder.querySelectorAll('path')
        paths.forEach((path) => {
          path.classList.add('blackText')
        })
      }
      if (event.target.id === 'chris') {
        const helloChris = document.getElementById('chris')
        const paths = helloChris.querySelectorAll('path')
        paths.forEach((path) => {
          path.classList.add('blackText')
        })
      }
      if (event.target.id === 'tomorrow') {
        const helloTomorrow = document.getElementById('tomorrow')
        const paths = helloTomorrow.querySelectorAll('path')
        paths.forEach((path) => {
          path.classList.add('blackText')
        })
        setTimeout(() => {
          window.location.form = 'https://www.tomorrow.tv/'
        }, 1000)
      }
    }
  }
}
</script>

